sysauto_script.script:
// Get today'd date and time
var rightnow =  new GlideDateTime();

// Query the database for NeedIt Task records with Due date field values older
	// than the current time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
var overdureTasks = new GlideRecord("x_402039_demo_app_demo_task");
overdureTasks.addQuery('due_date','>=',rightnow);
overdureTasks.addQuery('due_date','<',gs.hoursAgo(-24));

overdureTasks.query();

while(overdureTasks.next()){
		gs.info("Overdue Demo app task record: " + overdureTasks.number);         gs.eventQueue('x_402039_demo_app.dueSoonDemoappTasks',overdureTasks,overdureTasks.number,gs.getUserName());
}
